mapper.select:
select

from charge_off


        AND submitter_name = #{queryDTO.submitterName}


        AND charge_off_type = #{queryDTO.chargeOffType}


        AND status = #{queryDTO.stauts}


        AND city_code = #{queryDTO.cityCode}


        AND total_point = #{queryDTO.totalPoint}


        AND submitter_time >= #{queryDTO.submitterTimeBegin}


        AND submitter_time <= #{queryDTO.submitterTimeEnd}

    AND is_deleted = 0 ORDER BY submitter_time desc


    limit #{offset},#{limit}

select

from charge_off


        AND submitter_time >= #{fuzzyQueryDTO.submitterTimeBegin}


        AND submitter_time <= #{fuzzyQueryDTO.submitterTimeEnd}


        AND submitter_id = #{fuzzyQueryDTO.submitterId}


        AND status = #{fuzzyQueryDTO.stauts}


        OR bank_outlets_name like concat('%',#{fuzzyQueryDTO.condition},'%')
        OR customer_name like concat('%',#{fuzzyQueryDTO.condition},'%')
        OR customer_mobile like concat('%',#{fuzzyQueryDTO.condition},'%')
        OR product_value like concat('%',#{fuzzyQueryDTO.condition},'%')
        OR charge_off_point like concat('%',#{fuzzyQueryDTO.condition},'%')

    AND is_deleted = 0 ORDER BY submitter_time desc


    limit #{offset},#{limit}
