mapper.select:
select
FROM point where id in
#{item}

update.foreach:
update point
name = #{item.name},description = #{item.description},project_id = #{item.projectId},city_codes = #{item.cityCodes},product_ids = #{item.productIds},face_value = #{item.faceValue}, balance = #{item.balance},expiry_time = #{item.expiryTime},mod_time = NOW()
where id = #{item.id}

mapper.select:
SELECT p.id, p.name,p.description,p.customer_id, p.project_id,
p.city_codes,p.product_ids,p.face_value,p.balance,
p.exchange_record_id,p.expiry_time,p.status,p.ext_info, p.is_deleted,p.add_time, p.mod_time
FROM point p LEFT JOIN
point_exchange_record pec ON p.id = pec.point_id
LEFT JOIN customer c ON c.id = p.customer_id


        AND c.mobile = #{mobile}


        AND pec.exchange_type = #{exchangeType}


        AND p.status = #{status}


        AND p.expiry_time > NOW()


        AND p.expiry_time <= NOW()

    AND p.is_deleted = 0 ORDER BY p.add_time desc

limit #{offset},#{limit}

select count(*) FROM point p
LEFT JOIN point_exchange_record pec ON p.id = pec.point_id
LEFT JOIN customer c ON c.id = p.customer_id


        AND c.mobile = #{mobile}


        AND pec.exchange_type = #{exchangeType}


        AND p.status = #{status}


        AND p.expiry_time > NOW()


        AND p.expiry_time <= NOW()

    AND p.is_deleted = 0 ORDER BY p.add_time desc
