mapper.select:
SELECT rc.id, rc.name,rc.description,rc.customer_id, rc.project_id,
rc.city_codes,rc.product_ids,rc.count,rc.exchange_record_id,rc.expiry_time,rc.used,rc.status,rc.ext_info, rc.is_deleted,rc.add_time, rc.mod_time
FROM voucher rc LEFT JOIN
voucher_exchange_record rcec ON rc.id = rcec.voucher_id
LEFT JOIN customer c ON c.id = rc.customer_id


        AND c.mobile = #{mobile}


        AND rcec.exchange_type = #{exchangeType}


        AND rc.status = #{status}


        AND rc.expiry_time > NOW()


        AND rc.expiry_time <= NOW()

    AND rc.is_deleted = 0 ORDER BY rc.add_time desc

limit #{offset},#{limit}

select count(*) FROM voucher rc LEFT JOIN
voucher_exchange_record rcec ON rc.id = rcec.voucher_id
LEFT JOIN customer c ON c.id = rc.customer_id


        AND c.mobile = #{mobile}


        AND rcec.exchange_type = #{exchangeType}


        AND rc.status = #{status}


        AND rc.expiry_time > NOW()


        AND rc.expiry_time <= NOW()

    AND rc.is_deleted = 0 ORDER BY rc.add_time desc

select
FROM voucher where id in
#{item}

update.foreach:
update voucher
name = #{item.name},description = #{item.description},project_id = #{item.projectId},city_codes = #{item.cityCodes},product_ids = #{item.productIds},expiry_time = #{item.expiryTime},mod_time = NOW()
where id = #{item.id}
